import React, { Component } from 'react'
import './App.css';
import Input from './Input/InputClass';
import ButtonClass from './Button/Button';

export default class App extends Component {

  state = {
    userInput: {},
    formArray: [
      {
        input: {
          type: 'text',
          name: 'name',
          value: '',
          id: 'input-name',
          label: 'Name',
          required: true,
          style: {
            width: '250px',
            marginTop: '15px'
          }
        }
      },
      {
        input: {
          type: 'email',
          name: 'email',
          value: '',
          id: 'input-email',
          label: 'Email',
          required: true,
          style: {
            width: '250px',
            marginTop: '15px'
          }
        }
      },
      {
        input: {
          type: 'password',
          name: 'password',
          value: '',
          id: 'input-password',
          label: 'password',
          required: true,
          style: {
            width: '250px',
            marginTop: '15px'
          }
        }
      },
      {
        input: {
          type: 'select',
          name: 'gender',
          value: '',
          id: 'input-gender',
          label: 'Select',
          select: true,
          required: true,
          genders: ['male', 'female', 'undefined'],
          helperText: 'Please select your gender',
          style: {
            width: '250px',
            marginTop: '15px'
          }
        }
      },
      {
        input: {
          type: 'multiline',
          name: 'multiline',
          id: 'input-multiline',
          label: 'Tell us something',
          value: '',
          multiline: true,
          required: false,
          rows: 4,
          style: {
            width: '250px',
            marginTop: '15px'
          }
        }
      }
    ]
  }

  handleInputChange = (event) => {
    console.log(event.target.name, event.target.value)

    this.setState({
      userInput: {
        ...this.state.userInput,
        [event.target.name]: event.target.value
      }
    })

  }

  handleFormSubmit = (event) => {
    event.preventDefault();
    this.setState({
      userInput: {}
    })
    this.form.reset()
  }

  render() {
    return (
      <form className='App' ref={(ref) => this.form = ref} onSubmit={this.handleFormSubmit}>
        {
          this.state.formArray.map((field, index) => {

            return (
              <div key={field.input.label}>
                  <Input 
                    {...field}
                    {...this.state.userInput}
                    handleInputChange={this.handleInputChange}
                  />
                  <br />
              </div>
            )
          })
        }
         <br />

        <ButtonClass 
          color="primary"
          variant="contained"
          type="submit"
          >Submit
        </ButtonClass>

      </form>
    )
  }
}
