import React, { Component } from 'react'
import './App.css';
import Input from './Input/Input';
import ButtonClass from './Button/Button';
import TextField from '@material-ui/core/TextField';

export default class App extends Component {

  state = {
    formArray: [
      {
        input: {
          style: {
            width: '250px',
            marginTop: '5px'
          },
          id: 'input-name',
          label: 'Name',
          value: '',
          type: 'name',
          required: true,
          rows: 0
        }
      },
      {
        input: {
          style: {
            width: '250px',
            marginTop: '5px'
          },
          id: 'input-email',
          label: 'Email',
          value: '',
          type: 'email',
          required: true,
          rows: 0
        }
      },
      {
        input: {
          style: {
            width: '250px',
            marginTop: '5px'
          },
          id: 'input-password',
          label: 'password',
          value: '',
          type: 'password',
          required: true,
          rows: 0
        }
      },
      {
        input: {
          style: {
            width: '250px',
            marginTop: '5px'
          },
          id: 'input-multiline',
          label: 'Multiline',
          value: '',
          type: 'multiline',
          required: false,
          rows: 4
        }
      }
    ]
  }

  handleInputChange = (event) => {
    console.log(event.target.name, event.target.value)

    this.setState({
      [event.target.name]: event.target.value
    })

  }

  handleFormSubmit = (event) => {
    event.preventDefault();
    this.form.reset()
  }

  render() {
    return (
      <form className='App' ref={(ref) => this.form = ref} onSubmit={this.handleFormSubmit}>
        {
          this.state.formArray.map((field, index) => {

            return (
              <div key={field.input.label}>
                  <Input 
                    id={field.input.id}
                    label={field.input.label}
                    required={field.input.required}
                    style={field.input.style}
                    name={field.input.type}
                    handleInputChange={this.handleInputChange}
                    rows={field.input.rows}
                  />
                  <br />
              </div>
            )
          })
        }
         <br />

        <ButtonClass 
          color="primary"
          variant="contained"
          type="submit"
          >Submit</ButtonClass>
              <TextField
        id="filled-multiline-static"
        label="Multiline"
        multiline
        rows="4"
        margin="normal"

      />
      </form>
    )
  }
}
