import React, { Component } from 'react'
import ButtonClass from '../../Factory/Button/ButtonClass'
import formArray from './CreateTalkConfig';
import { ValidatorForm } from 'react-material-ui-form-validator';
import Input from '../../Factory/Input/InputClass';
import Spinner from '../../Factory/Spinner/Spinner';

export default class CreateTalk extends Component {

  state = {
    formData: {
      title: '',
      talk: '',
      picture: ''
    },
    submitted: false,
    uploadPictureToggle: false
  }

  uploadWidget = () => {
    window.cloudinary.openUploadWidget({ cloud_name: 'dy6xduf53', upload_preset: 'gxaofwpa', tags:['hamster']},
    (error, result)  => {
        if (error) {
          console.log(error)
        } else {

          if (result.event === 'success') {
            console.log(result.info.secure_url)
            const { formData } = this.state;
            formData['picture'] = result.info.secure_url
            this.setState({
              ...this.state, 
              formData, 

            })
          }

        }
    });
  }

  handleSubmit = (event) => {
    event.preventDefault();

    console.log(this.state)
  }

  handleChange = (event) => {

    const { formData } = this.state;
    formData[event.target.name] = event.target.value;
    this.setState({ formData });
  }

  render() {

    const {submitted} = this.state;

    let form = (
      formArray.map((field, index) => {

        return (
          <div key={field.input.label}>
              <Input 
                {...field}
                {...this.state.formData}
                handleInputChange={this.handleChange}
              />
              <br />
          </div>
        )
      })
    ) 

    return (
      <div className='App'>

     <ValidatorForm className='Form' onSubmit={this.handleSubmit}>
            {
                submitted ? <Spinner /> : form
            }
            <br />

            <ButtonClass 
              onClick={this.uploadWidget}
              color="primary"
              variant="outlined"
              type="submit"
              >
                Add Image
           </ButtonClass>

            <div style={{height: '15px',margin: '10 10'}}></div>
            <ButtonClass 
              color="primary"
              variant="contained"
              type="submit"
              disabled={submitted}
              > 
              {
                  (submitted && 'Your form is submitted!')
                  || (!submitted && 'Submit')
              }
            </ButtonClass>

          </ValidatorForm>



      </div>
    )
  }
}
