import React, { Component } from 'react'
import './App.css';
import Input from './Input/Input'

export default class App extends Component {

  state = {
    formArray: [
      {
        input: {
          style: {
            width: 200
          },
          id: 1,
          label: 'Name',
          value: '',
        }
      },
      {
        input: {
          style: {
            width: 200
          },
          id: 2,
          label: 'Email',
          value: ''
        }
      }
    ]
  }


  render() {
    return (
      <form className='App'>
        {
          this.state.formArray.map((field, index) => {

            return (
              <>
                  <Input 
                    key={field.input.id}     
                  />
                  <br />
              </>
            )
          })
        }
      </form>
    )
  }
}
