import { AUTH_USER_SUCCESSFUL, AUTH_USER_LOGOUT, AUTH_SIGNUP_SUCCESSFUL } from '../actionTypes/actionTypes';
import setAuthToken from '../../lib/setAuthToken';
import Axios from '../../lib/Axios';

export const signup = (userInfo) => async dispatch => {

  try {
    let success = await Axios.post('/users/sign-up', userInfo);
    console.log(success)
    messageHandler(success);

  } catch (error) {
    return error;
  }

}

export const logout = () => dispatch => {
   localStorage.removeItem('jwtToken');
   setAuthToken(false);
   dispatch({
     type: AUTH_USER_LOGOUT
   });
}

export const setAuthSuccessUser = (userInfo) => dispatch => {
  dispatch({
    type: AUTH_USER_SUCCESSFUL,
    payload: userInfo
  })
}

const messageHandler = (success) => dispatch => {

  dispatch({
    type: AUTH_SIGNUP_SUCCESSFUL,
    payload: success
  })

}
