/* eslint-disable no-unused-expressions */
/* eslint-disable no-duplicate-case */
/* eslint-disable no-fallthrough */
import React from 'react';
import {TextField, MenuItem} from '@material-ui/core';




const Input = (props) => {
  const { input } = props;

  let dynamicInputField = null 

  switch (input.type) {

    case('text'):
      dynamicInputField = <TextField 
                                id={input.id}
                                label={input.label}
                                required={input.required}
                                style={input.style}
                                name={input.name}
                                type={input.name}
                                onChange={input.handleInputChange}
                          />
      break;

    case('email'):
        dynamicInputField = <TextField 
                              id={input.id}
                              label={input.label}
                              required={input.required}
                              style={input.style}
                              name={input.name}
                              type={input.name}
                              onChange={input.handleInputChange}
                            />
        break;                   

    case('password'):
        dynamicInputField = <TextField 
                              id={input.id}
                              label={input.label}
                              required={input.required}
                              style={input.style}
                              name={input.name}
                              type={input.name}
                              onChange={input.handleInputChange}
                            />
        break; 
    case('select'):

        console.log(input.select)
        dynamicInputField = <TextField
                              id={input.id}
                              select={input.select}
                              label={input.label}
                              style={input.style}
                              name={input.name}
                              onChange={input.handleInputChange}
                              helperText={input.helperText}
                              SelectProps={{
                                MenuProps: {
                                  className: {
                                    width: 200
                                  }
                                },
                              }}
                            >
                            {
                              input.gender.map(option => {
                                return (
                                  <MenuItem key={option} value={option}>
                                  {option}
                                </MenuItem>
                                )
                            })}
                          </TextField>
    case('multiline'):
      dynamicInputField = <TextField 
                            id={input.id}
                            label={input.label}
                            required={input.required}
                            style={input.style}
                            name={input.name}
                            type={input.name}
                            onChange={input.handleInputChange}
                            multiline={input.multiline}
                          />

      break;

    default: 
      return null;
  }



  return (
    <>
      {dynamicInputField}
    </>
  )
}

export default Input
