import React, { Component } from 'react'
import './App.css';
import Input from './Input/Input';
import ButtonClass from './Button/Button';

export default class App extends Component {

  state = {
    formArray: [
      {
        input: {
          type: 'text',
          value: '',
          id: 'input-name',
          label: 'Name',
          required: true,
          style: {
            width: '250px',
            marginTop: '5px'
          }
        }
      },
      {
        input: {
          type: 'email',
          value: '',
          id: 'input-email',
          label: 'Email',
          required: true,
          style: {
            width: '250px',
            marginTop: '5px'
          }
        }
      },
      {
        input: {
          type: 'password',
          value: '',
          id: 'input-password',
          label: 'password',
          required: true,
          style: {
            width: '250px',
            marginTop: '5px'
          }
        }
      },
      {
        input: {
          type: 'select',
          value: '',
          id: 'input-gender',
          label: 'Select',
          select: true,
          required: true,
          gender: ['male', 'female', 'undefined'],
          helperText: 'Please select your gender',
          style: {
            width: '250px',
            marginTop: '5px'
          }
        }
      },
      {
        input: {
          type: 'multiline',
          id: 'input-multiline',
          label: 'Tell us something',
          value: '',
          multiline: true,
          required: false,
          rows: 4,
          style: {
            width: '250px',
            marginTop: '5px'
          }
        }
      }
    ]
  }

  handleInputChange = (event) => {
    console.log(event.target.name, event.target.value)

    this.setState({
      [event.target.name]: event.target.value
    })

  }

  handleFormSubmit = (event) => {
    event.preventDefault();
    this.form.reset()
  }

  render() {
    return (
      <form className='App' ref={(ref) => this.form = ref} onSubmit={this.handleFormSubmit}>
        {
          this.state.formArray.map((field, index) => {

            return (
              <div key={field.input.label}>
                  <Input 
                    {...field}
                    handleInputChange={this.handleInputChange}
                  />
                  <br />
              </div>
            )
          })
        }
         <br />

        <ButtonClass 
          color="primary"
          variant="contained"
          type="submit"
          >Submit</ButtonClass>

      </form>
    )
  }
}
