/* eslint-disable no-unused-expressions */
/* eslint-disable no-duplicate-case */
/* eslint-disable no-fallthrough */
import React from 'react';
import {TextField, MenuItem, FormControl, InputLabel, Select, Input} from '@material-ui/core';




const InputClass = (props) => {
  const { input } = props;

  let dynamicInputField = null 


  switch (input.type) {

    case('text'):

      dynamicInputField = <TextField 
                                id={input.id}
                                label={input.label}
                                required={input.required}
                                style={input.style}
                                name={input.name}
                                type={input.type}
                                onChange={props.handleInputChange}
                          />
      break;

    case('email'):
        dynamicInputField = <TextField 
                              id={input.id}
                              label={input.label}
                              required={input.required}
                              style={input.style}
                              name={input.name}
                              type={input.type}
                              onChange={props.handleInputChange}
                            />
        break;                   

    case('password'):
        dynamicInputField = <TextField 
                              id={input.id}
                              label={input.label}
                              required={input.required}
                              style={input.style}
                              name={input.name}
                              type={input.type}
                              onChange={props.handleInputChange}
                            />
        break; 
    case('select'):
        console.log(input)
        dynamicInputField = <FormControl style={input.style}>
                              <InputLabel htmlFor="select-multiple">Choose You Gender</InputLabel>
                              <Select
                                value={'female'}
                                onChange={props.handleInputChange}

                                name={input.name}
                              >
                              {
                                input.gender.map(option => {
                                  return (
                                    <MenuItem key={option} value={option}>
                                      {option}
                                    </MenuItem>
                                  )
                              })}
                              </Select>
                              </FormControl>


      break;
    case('multiline'):

      dynamicInputField = <TextField 
                            id={input.id}
                            label={input.label}
                            required={input.required}
                            style={input.style}
                            name={input.name}
                            type={input.name}
                            onChange={props.handleInputChange}
                            rows={input.rows}
                            multiline={input.multiline}
                          />

      break;

    default: 
      return null;
  }



  return (
    <>
      {dynamicInputField}
    </>
  )
}

export default InputClass
