          }

        }
    });
  }

  render() {

    const {submitted} = this.state;

    return (
      <div className='App'>

     <ValidatorForm className='Form' onSubmit={this.handleSubmit}>
            {
                submitted ? <Spinner /> : form
            }
            <br />

            <ButtonClass 
              color="primary"
              variant="contained"
              type="submit"
              disabled={submitted}
              > 
              {
                  (submitted && 'Your form is submitted!')
                  || (!submitted && 'Submit')
              }
            </ButtonClass>

          </ValidatorForm>


        <ButtonClass 
          onClick={this.uploadWidget}
          color="primary"
          variant="contained"
          type="submit"
          >
            Add Image
        </ButtonClass>
      </div>
    )
  }
}
