import { AUTH_USER_SUCCESSFUL, AUTH_USER_LOGOUT, AUTH_SIGNUP_SUCCESSFUL } from '../actionTypes/actionTypes';

const initialState = {
  isAuthenticated: false,
  user: {}
};

export default function(state = initialState, action) {
  switch (action.type) {
    case AUTH_USER_SUCCESSFUL:
      console.log(action.payload)
      return {
        ...state,
        isAuthenticated: true,
        user: action.payload
      };
    case AUTH_USER_LOGOUT:
      return {
        isAuthenticated: false,
        user: {}
      }
    default:
      return state;
  }
}
