import React, { Component } from 'react'
import './Signup.css';
import Input from '../../Factory/Input/InputClass'
import ButtonClass from '../../Factory/Button/ButtonClass';
import { ValidatorForm } from 'react-material-ui-form-validator';

export default class Signup extends Component {

  state = {
    formData: {
      name: '',
      email: '',
      password: '',
      confirmPassword: ''
    },
    submitted: false,
    formArray: [
      {
        input: {
          type: 'text',
          name: 'name',
          id: 'input-name',
          label: 'Name',
          style: {
            width: '250px',
            marginTop: '15px'
          },
          validators: ['required'],
          errorMessages: ['this field is required']
        }
      },
      {
        input: {
          type: 'email',
          name: 'email',
          id: 'input-email',
          label: 'Email',
          style: {
            width: '250px',
            marginTop: '15px'
          },
          validators: ['required', 'isEmail'],
          errorMessages: ['this field is required', 'email is not valid']
        }
      },
      {
        input: {
          type: 'password',
          name: 'password',
          id: 'input-password',
          label: 'password',
          style: {
            width: '250px',
            marginTop: '15px'
          },
          validators: ['required'],
          errorMessages: ['this field is required']
        }
      },
      {
        input: {
          type: 'confirmPassword',
          name: 'confirmPassword',
          id: 'input-confirm-password',
          label: 'confirm-password',
          style: {
            width: '250px',
            marginTop: '15px'
          },
          validators: ['isPasswordMatch', 'required'],
          errorMessages: ['password mismatch', 'this field is required']
        }
      },
      {
        input: {
          type: 'select',
          name: 'gender',
          value: '',
          id: 'input-gender',
          label: 'Select',
          select: true,
          required: true,
          genders: ['male', 'female', 'undefined'],
          helperText: 'Please select your gender',
          style: {
            width: '250px',
            marginTop: '15px'
          },
          validators: ['required'],
          errorMessages: ['this field is required']
        }
      }
    ]
  }

  componentDidMount() {
    // custom rule will have name 'isPasswordMatch'
    ValidatorForm.addValidationRule('isPasswordMatch', (value) => {
        const { formData } = this.state;
        if (value !== formData.password) {
            return false;
        }
        return true;
    });
  }


  handleChange = (event) => {
    const { formData } = this.state;
    formData[event.target.name] = event.target.value;
    this.setState({ formData });
  }

  handleSubmit = () => {
      this.setState({ submitted: true }, () => {
          setTimeout(() => this.setState({ submitted: false }), 5000);
      });
  }


  render() {

    const {submitted } = this.state;

    return (
      <ValidatorForm className='Form' ref={(ref) => this.form = ref} onSubmit={this.handleSubmit}>
        {
          this.state.formArray.map((field, index) => {

            return (
              <div key={field.input.label}>
                  <Input 
                    {...field}
                    {...this.state.formData}
                    handleInputChange={this.handleChange}
                  />
                  <br />
              </div>
            )
          })
        }
         <br />

        <ButtonClass 
          color="primary"
          variant="contained"
          type="submit"
          disabled={submitted}
          > 
          {
              (submitted && 'Your form is submitted!')
              || (!submitted && 'Submit')
          }
        </ButtonClass>

      </ValidatorForm>
    )
  }
}
