import React, { Component } from 'react'
import './App.css';
import Input from './Input/Input'

export default class App extends Component {

  state = {
    formArray: [
      {
        nameInput: {
          style: {
            width: 200
          },
          id: 'input-name',
          label: 'Name',
          value: ''
        }
      }
    ]
  }


  render() {
    return (
      <form className='App'>
        {
          this.state.formArray.map((inputValue, index) => {
            return (
              <Input />
            )
          })
        }
      </form>
    )
  }
}
