import React, { Component } from 'react'
import { connect } from 'react-redux';
import './Signup.css';
import Input from '../../Factory/Input/InputClass'
import ButtonClass from '../../Factory/Button/ButtonClass';
import { ValidatorForm } from 'react-material-ui-form-validator';
import formArray from './SignUpConfig';
import { signup, handleErrorSignup } from '../../redux/action/authUserAction';
import Spinner from '../../Factory/Spinner/Spinner'
import MessageBar from '../../Factory/MessageBar/MessageBar';


class Signup extends Component {

  state = {
    formData: {
      username: '',
      email: '',
      password: '',
      confirmPassword: '',
      gender: '',
      redirectSecond: 0
    },
    submitted: false,
  }

  componentDidMount() {
    // custom rule will have name 'isPasswordMatch'
    ValidatorForm.addValidationRule('isPasswordMatch', (value) => {
        const { formData } = this.state;
        if (value !== formData.password) {
            return false;
        }
        return true;
    });
  }

  countdownSecond = () => {

    let startCount = 3;

    let timer = setInterval(() => {

      while(startCount >= 0) {
        this.setState({
          redirectSecond: startCount
        })
        startCount--;

        if (startCount >= 0) {
          clearInterval(timer)
        }


      }
    }, 1);



  }


  handleChange = (event) => {
    const { formData } = this.state;
    formData[event.target.name] = event.target.value;
    this.setState({ formData });
  }



  handleSubmit = (event) => {

      event.preventDefault();


      this.setState({ 
          submitted: true, 
        }, () => {

          this.props.signup(this.state.formData)
              .then(success => {

                this.setState({
                  submitted: false,
                  formData: {
                    name: '',
                    email: '',
                    password: '',
                    confirmPassword: '',
                    gender: ''
                  }
                })

                setTimeout(() => {
                  this.countdownSecond()
                }, 1)      


              })
              .catch(error => {

                this.props.handleErrorSignup(error.response.data.message)
                this.setState({
                  submitted: false
                })
              })
      });
  }

  render() {

    const {submitted} = this.state;

    let form = (
      formArray.map((field, index) => {

        return (
          <div key={field.input.label}>
              <Input 
                {...field}
                {...this.state.formData}
                handleInputChange={this.handleChange}
              />
              <br />
          </div>
        )
      })
    ) 

    return (


      <>  

      {this.props.message.serverMessage !== null ? <MessageBar 
                                              fontColorStyle={this.props.message.messageStyle.fontColorStyle}
                                              dynamicClassName={this.props.message.messageStyle.dynamicClassName}
                                              >{this.props.message.serverMessage} Redirecting you in {this.state.redirectSecond}</MessageBar> : '' }


      <ValidatorForm className='Form' onSubmit={this.handleSubmit}>
            {
                submitted ? <Spinner /> : form
            }
            <br />

            <ButtonClass 
              color="primary"
              variant="contained"
              type="submit"
              disabled={submitted}
              > 
              {
                  (submitted && 'Your form is submitted!')
                  || (!submitted && 'Submit')
              }
            </ButtonClass>

          </ValidatorForm>

      </>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    message: state.message
  }
}

export default connect(mapStateToProps, { signup, handleErrorSignup })(Signup)
