import React, { Component } from 'react'
import { connect } from 'react-redux';

import './Signup.css';
import Input from '../../Factory/Input/InputClass'
import ButtonClass from '../../Factory/Button/ButtonClass';
import { ValidatorForm } from 'react-material-ui-form-validator';
import formArray from './SignUpConfig';
import { authUserSuccessful } from '../../redux/action/authUserAction';


class Signup extends Component {

  state = {
    formData: {
      name: '',
      email: '',
      password: '',
      confirmPassword: '',
      gender: ''
    },
    submitted: false,
  }

  componentDidMount() {
    // custom rule will have name 'isPasswordMatch'
    ValidatorForm.addValidationRule('isPasswordMatch', (value) => {
        const { formData } = this.state;
        if (value !== formData.password) {
            return false;
        }
        return true;
    });
  }


  handleChange = (event) => {
    const { formData } = this.state;
    formData[event.target.name] = event.target.value;
    this.setState({ formData });
  }

  handleSubmit = (event) => {

      event.preventDefault();


      this.setState({ submitted: true }, () => {
          setTimeout(() => this.setState({ submitted: false }), 5000);
      });

        console.log(this.form)
  }


  render() {

    const {submitted } = this.state;

    return (
      <ValidatorForm className='Form' ref={(ref) => this.form = ref} onSubmit={this.handleSubmit}>
        {
            formArray.map((field, index) => {

            return (
              <div key={field.input.label}>
                  <Input 
                    {...field}
                    {...this.state.formData}
                    handleInputChange={this.handleChange}
                  />
                  <br />
              </div>
            )
          })
        }
         <br />

        <ButtonClass 
          color="primary"
          variant="contained"
          type="submit"
          disabled={submitted}
          > 
          {
              (submitted && 'Your form is submitted!')
              || (!submitted && 'Submit')
          }
        </ButtonClass>

      </ValidatorForm>
    )
  }
}

export default connect(null, { authUserSuccessful })(Signup)
