import React, { Component } from 'react'
import Talk from '../Talk/Talk'
import Grid from '@material-ui/core/Grid';
import { withStyles } from '@material-ui/core/styles';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom'
import {getAllUserTalks} from '../../redux/action/talkAction';

const styles = {
  root: {
    flexGrow: 1,
    marginTop: 15,
  }
}

class AllUserTalks extends Component {


  componentDidMount() {
    this.props.getAllUserTalks(this.props.authUser.user.id)
        .then( allUserTalks => {
          console.log(allUserTalks)
        })
        .catch(error => {
          console.log(error)
        })
  }


  render() {

    const { userTalks } = this.props.talk; 
    console.log(this.props.match.path)

    return (
      <div className={this.props.classes.root}>
             <Grid container justify="center"  spacing={1}>
          {
            userTalks.map((talk) => {
              return (
                <Grid key={talk._id}  item>
                  <Talk {...talk} />
                </Grid>
              )
            })
          }
      </Grid>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    talk: state.talk,
    authUser: state.authUser
  }
}

export default connect(mapStateToProps, { getAllUserTalks })(withRouter(withStyles(styles)(AllUserTalks)));
