import React, { Component } from 'react'
import './App.css';
import Input from './Input/Input'

export default class App extends Component {

  state = {
    formArray: [
      {
        input: {
          style: {
            width: '250px'
          },
          id: 'input-name',
          label: 'Name',
          value: '',
          type: 'name',
          required: true
        }
      },
      {
        input: {
          style: {
            width: '250px'
          },
          id: 'input-email',
          label: 'Email',
          value: '',
          type: 'email',
          required: true
        }
      }
    ]
  }

  handleInputChange = (event) => {
    console.log(event.target.name, event.target.value)

    this.setState({
      [event.target.name]: event.target.value
    })

  }


  render() {
    return (
      <form className='App'>
        {
          this.state.formArray.map((field, index) => {

            return (
              <div key={field.input.label}>
                  <Input 
                    id={field.input.id}
                    label={field.input.label}
                    required={field.input.required}
                    style={field.input.style}
                    name={field.input.type}
                    handleInputChange={this.handleInputChange}
                  />
                  <br />
              </div>
            )
          })
        }
      </form>
    )
  }
}
